// Parse the string output from AI into a JSON object
const aiOutput = $input.first().json.output;

// Sometimes AI wraps in markdown code blocks, remove them if present
const cleanJson = aiOutput.replace(/```json/g, '').replace(/```/g, '').trim();

// Returns the array directly. n8n will treat this as multiple items.
return JSON.parse(cleanJson);
